                Sum_4 = bigAdd(Sum_3, Ip_2),
                Diff_4 = bigAbsDiff(Sum_4, Prev_3),
                case ((DenomPow_3 >= 70) andalso (bigCmp(Diff_4, bigMulPow10(bigFromInt(1), (DenomPow_3 - 70))) < 0)) of
