using System;
using System.Collections.Generic;
using System.Linq;

struct Product {
    public string name;
    public int price;
    public override string ToString() => $"{{\"name\": \"{name}\", \"price\": {price}}}";
}
class Program {
    static Product[] products = new Product[]{new Product{name = "Laptop", price = 1500}, new Product{name = "Smartphone", price = 900}, new Product{name = "Tablet", price = 600}, new Product{name = "Monitor", price = 300}, new Product{name = "Keyboard", price = 100}, new Product{name = "Mouse", price = 50}, new Product{name = "Headphones", price = 200}};
    static Product[] expensive = (((from p in products orderby p.price descending select p).Skip(1)).Take(3)).ToArray();
    static void Main() {
        Console.WriteLine("--- Top products (excluding most expensive) ---");
        foreach (var item in expensive) {
    Console.WriteLine(string.Join(" ", new object[]{item.name, "costs $", item.price}).TrimEnd());
}
    }
}
