# Generated by Mochi transpiler on 2025-07-21 07:06 +0700
people = [%{name: "Alice", age: 30}, %{name: "Bob", age: 15}, %{name: "Charlie", age: 65}, %{name: "Diana", age: 45}]
adults = for person <- people, person.age >= 18 do
  %{
    name: person.name,
    age: person.age,
    is_senior: person.age >= 60
  }
end
IO.puts("--- Adults ---")
Enum.each(adults, fn person ->
  IO.puts("#{person.name} is #{person.age} #{if person.is_senior, do: " (senior)", else: ""}")
end)
