# Generated by Mochi transpiler on 2025-07-21 19:10 +0700
customers = [%{id: 1, name: "Alice"}, %{id: 2, name: "Bob"}]
orders = [%{id: 100, customerId: 1}, %{id: 101, customerId: 2}]
items = [%{orderId: 100, sku: "a"}, %{orderId: 101, sku: "b"}]
result = for o <- orders, c <- customers, i <- items, o.customerId == c.id && o.id == i.orderId do
  %{
    name: c.name,
    sku: i.sku
  }
end
IO.puts("--- Multi Join ---")
Enum.each(result, fn r ->
  IO.puts("#{Kernel.inspect(r.name)} bought item #{Kernel.inspect(r.sku)}")
end)
